mapper.select:
select m.id,m.openId,m.openType,m.openState,m.openRemark,m.bindTime,m.userId
from xunxin_user_info_data m
where m.phone=#{phone}

select m.id,m.openId,m.openType,m.openState,m.openRemark,m.bindTime,m.userId
from xunxin_user_info_data m
where m.userId=#{userId}

select m.id,m.openId,m.openType,m.openState,m.openRemark,m.bindTime,m.userId
from xunxin_user_info_data m
where 

	m.openId = #{openId} 


	and m.openType = #{openType}
